import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts"

const ConvergenceChart = ({ historial }) => {
  const data = historial.map((fitness, index) => ({
    generacion: index + 1,
    fitness: fitness,
  }))

  const CustomTooltip = ({ active, payload }) => {
    if (active && payload && payload.length) {
      return (
        <div
          style={{
            backgroundColor: "#0f0f0f",
            border: "1px solid #1f1f1f",
            borderRadius: "8px",
            padding: "12px",
            color: "#e5e5e5",
          }}
        >
          <p style={{ margin: 0, fontSize: "12px", color: "#a3a3a3" }}>Generación: {payload[0].payload.generacion}</p>
          <p style={{ margin: "4px 0 0 0", fontSize: "14px", fontWeight: 600, color: "#0070f3" }}>
            Fitness: ${payload[0].value.toFixed(2)}
          </p>
        </div>
      )
    }
    return null
  }

  return (
    <ResponsiveContainer width="100%" height={400}>
      <LineChart data={data}>
        <CartesianGrid strokeDasharray="3 3" stroke="#1f1f1f" />

        <XAxis
          dataKey="generacion"
          label={{ value: "Generación", position: "insideBottom", offset: -5, fill: "#a3a3a3" }}
          stroke="#525252"
          tick={{ fill: "#a3a3a3" }}
        />
        <YAxis
          label={{ value: "Fitness (Ganancia)", angle: -90, position: "insideLeft", fill: "#a3a3a3" }}
          stroke="#525252"
          tick={{ fill: "#a3a3a3" }}
        />

        <Tooltip content={<CustomTooltip />} />

        <Legend wrapperStyle={{ color: "#e5e5e5" }} />

        <Line
          type="monotone"
          dataKey="fitness"
          stroke="#0070f3"
          strokeWidth={3}
          dot={false}
          name="Mejor Fitness"
          activeDot={{ r: 6, fill: "#0070f3", stroke: "#fff", strokeWidth: 2 }}
        />
      </LineChart>
    </ResponsiveContainer>
  )
}

export default ConvergenceChart
