import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts"

const ConvergenceChart = ({ historial }) => {
  const data = historial.map((fitness, index) => ({
    generacion: index + 1,
    fitness: fitness,
  }))

  return (
    <ResponsiveContainer width="100%" height={400}>
      <LineChart data={data}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="generacion" label={{ value: "Generación", position: "insideBottom", offset: -5 }} />
        <YAxis label={{ value: "Fitness (Ganancia)", angle: -90, position: "insideLeft" }} />
        <Tooltip />
        <Legend />
        <Line type="monotone" dataKey="fitness" stroke="#3b82f6" strokeWidth={2} dot={false} name="Mejor Fitness" />
      </LineChart>
    </ResponsiveContainer>
  )
}

export default ConvergenceChart
